import gql from  'graphql-tag';
import { useQuery } from '@apollo/react-hooks'
import { Card, ResourceList, Stack, TextStyle, Thumbnail } from '@shopify/polaris';
import store from 'store-js';
import { Item } from '@shopify/polaris/types/latest/src/components/FormLayout/components';

const GET_PRODUCTS_BY_ID = gql`
query getProducts($ids: [ID!]! ){
    nodes(ids: $ids){
      ... on Product{
        title
        handle
        id
        images(first: 1){
          edges{
            node{
              originalSrc
              altText
            }
          }
        }
        variants(first: 1){
          edges{
            node{
              price
              id
            }
          }
        }
      }
    }
  }
`;

function ProductList(){

    const { loading, error, data } = useQuery(GET_PRODUCTS_BY_ID, {variables: { ids: store.get('ids')}})

    if (loading) return <div>loading...</div>

if(error) return <div>{ error.message }</div>

console.log('this is data', data)
    return(
       <div>
            <card>
              <ResourceList
              showHeader
              resourceName = {{ singular: "Product", plural: "Products"}}
              items = { data.nodes }
              renderItem = {Item =>{
                const media=(
                  <Thumbnail 
                    source = {
                      item.images.edges[0] ? item.images.edges[0].originalSrc : ''
                    }
                    alt={
                      item.images.edges[0] ? item.images.edges[0].altText : ''
                    }
                  />
                );
                const price = item.variants.edges[0].node.price;
                return(
                  <ResourceList.Item 
                     id = {item.id}
                     media ={media}
                     accessibilityLabel={'view details for ${item.title}'}
                  >
                  <stack>
                     <Stack.Item fill>
                        <TextStyle variation="strong"> 
                            {item.title}
                        </TextStyle>
                     </Stack.Item>
                     <stack.Item>
                       <p>${price}</p>
                     </stack.Item>
                  </stack>
                  </ResourceList.Item>
                )
              }}
              />
                
            </card>
       </div>
    )
}

export default ProductList;
