import React, { useState } from 'react';
import {EmptyState, Layout, Page} from "@shopify/polaris";
import { fromAction } from "@shopify/app-bridge/actions/Error";
import { ResourcePicker } from '@shopify/app-bridge-react';
import store from 'store-js';
import ProductList from './components/ProductsList';

function Index(){

    const [modal, setModal] = useState({ open: false })
    const emptyState = !store.get('ids');
    function handleSection(resources){
        const idsFromResources = resources.selection.map((product) => product.id);
        setModal({ open: false });
        store.set('ids', idsFromResources)
        console.log('this is product ids', store.get('ids'));
    }
    return(
       <Page>
           <ResourcePicker
            resourceType="Product"
            showVariants={false}
            open={ modal.open } 
            onCancel={() => setModal({ open: false })}
            onSelection={(resources) => handleSection(resources)}

           >

           </ResourcePicker>
           
                { emptyState ?
                 <Layout>
            <EmptyState
               heading="Manage your inventory transfers"
               action={{content: 'Add Products',
               onAction : () => setModal({ open: true})
            }
             }
               secondaryAction={{content: 'Learn more', url: 'https://help.shopify.com'}}
               image="https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg"
               
            >
            <p>Track and receive your incoming inventory from suppliers.</p>
            </EmptyState>
            </Layout>
            :
            <ProductList />
            }
            
        </Page>
    )
}

export default Index;
